import { Component, NgModule, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { getBaseUrl } from '../../main';

@Component({
  selector: 'app-add-client',
  templateUrl: './add-client.component.html',
  styleUrls: ['./add-client.component.css']
})




export class AddClientComponent {
  firstName!: string;
  lastName!: string;

  constructor(private http: HttpClient) {
    @Inject('BASE_URL') getBaseUrl: String;

  }

  onSubmit() {
    // Send data to the server-side controller for saving to the database.
    const data = {
      firstName: this.firstName,
      lastName: this.lastName
    };

    console.log("test");


    const apiEndpoint = getBaseUrl + '/InsertAddClient';

    this.http.post(apiEndpoint, data).subscribe(
      (response) => {
        // Handle the successful response from the server here.
        console.log('Data saved successfully', response);
      },
      (error) => {
        // Handle any errors that occur during the HTTP request.
        console.error('Error saving data', error);
      }
    );


  }
}
