import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router'

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})


export class AppComponent {
  title = 'app';
  isLoginPage: boolean;

  constructor(private route: ActivatedRoute) {
    // Subscribe to route changes to update the 'isLoginPage' variable
    this.route.url.subscribe(urlSegments => {
      // Get the current route segment (if available)
      const currentSegment = urlSegments.length > 0 ? urlSegments[0].path : '';

      this.isLoginPage = currentSegment === 'login';

      console.log(currentSegment); // Log the current route segment
      console.log(this.isLoginPage, "testtest");
    });
  }


  }

}

<body>
  <app-nav-menu ></app-nav-menu>
  <main class="container">
    <router-outlet></router-outlet>
  </main>
</body>
