import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { CookieService } from 'ngx-cookie-service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  Username : string;
  Password: string;
  baseUrl: string;
  apiResponse: any;

  constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string, private router: Router, private cookieService: CookieService) {
    this.baseUrl = baseUrl;
  }


  loginbutton() {
    const data = {
      Username: this.Username,
      Password: this.Password,
    };


    const apiEndpoint = this.baseUrl + 'AddClientCnt/LoginUser';



    this.http.post(apiEndpoint, data).subscribe(
      (response) => {

        this.apiResponse = response;

        if (this.apiResponse.message == "Success") {

          console.log(this.apiResponse.token,"token");

          alert('Logged In Successfully.');
          localStorage.setItem('token', this.apiResponse.token);

          this.cookieService.set('username', data.Username);


          this.router.navigate(['/home']);

        }
        else {
          alert('Logged In Failed');
        }
      },
      (error) => {
        // Handle any errors that occur during the HTTP request.
        console.error('Error saving data', error);
      }
    )

  }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Login Page</title>
  <!-- Include Bootstrap CSS -->
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css">
</head>
<body>
  <div class="container">
    <div class="row justify-content-center">
      <div class="col-md-4">
       
          <h2 class="mb-4">Login</h2>

          <div class="form-group">
            <label for="username" >Username</label>
            <input type="text" [(ngModel)]="Username" class="form-control" id="username" placeholder="Enter your username">
          </div>

          <div class="form-group">
            <label for="password">Password</label>
            <input type="password" [(ngModel)] ="Password"  class="form-control" id="password" placeholder="Enter your password">
          </div>

          <button (click) ="loginbutton()"  class="btn btn-primary btn-block">Login</button>
    
      </div>
    </div>
  </div>

  <!-- Include Bootstrap JS (optional) -->
  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
</body>
</html>
